for (i = 1; i <= 10; i++) { 
 writeOutput(i); 
 }

// Define our array 
 myArray = ["a", "b", "c"]; 
 // By index 
 // Note the use of the newer member function syntax for arrayLen() 
 for (i = 1; i <= myArray.len(); i++) { 
 writeOutput(myArray[i]); 
 } 
 // By array 
 for (currentIndex in myArray) { 
 writeOutput(currentIndex); 
 } 
 // By arrayEach() 
 myArray.each(function(element, index) { 
   writeOuput(element & " : " & index); 
 });

arrayEach(["a","b","c"], function(element,index,array){ 
 writeOutput("#index#:#element#;"); // 1:a;2:b;3:c; 
 });

a = ["a","b","c"]; 
 a.each(function(element,index,array){ 
 writeOutput("#index#:#element#;"); // 1:a;2:b;3:c; 
 });

// Define our struct 
 myStruct = {name: "Tony", state: "Florida"}; 
 // By struct 
 for (currentKey in myStruct) { 
 writeOutput("<li>#currentKey# : #myStruct[currentKey]#</li>"); 
 } 
 // By structEach() 
 myStruct.each(function(key, value) { 
 writeOutput("<li>#key# : #value#</li>"); 
 });

structEach(struct, function(key,value,struct){ 
 writeOutput("#key#:#value#;"); // a:1;b:2;c:3; 
 });

struct.each(function(key,value,struct){ 
 writeOutput("#key#:#value#;"); // a:1;b:2;c:3; 
 });

// Define our list 
 myList = "a, b, c"; 
 // By array 
 for (item in listToArray(myList, ",")) { 
 writeOutput(item); 
 } 
 // By listEach() 
 myList.each(function(element, index) { 
 writeOuput(element & " : " & index); 
 }, ",");

list = "a;b;c"; 
 listEach(list, function(element,index,list){ 
 writeOutput("#index#:#element#;"); // 1:a;2:b;3:c; 
 }, ";");

list = "a;b;c"; 
 list.each(function(element,index,list){ 
 writeOutput("#index#:#element#;"); // 1:a;2:b;3:c; 
 }, ";");

// Define our query 
 platform = ["Adobe ColdFusion", "Railo", "Lucee"]; 
 myQuery = queryNew(" "); 
 queryAddColumn(myQuery, "platform", "CF_SQL_VARCHAR", platform); 
 // By row index 
 for (i = 1; i <= myQuery.recordCount; i++) { 
 writeOutput("<li>#myQuery["platform"][i]#</li>"); 
 } 
 // By query 
 for (row in myQuery) { 
 writeOutput("<li>#row.platform#</li>"); 
 }

q = queryNew("pk,fk,data", "integer,integer,varchar",[ 
 [1, 10, "aa"], 
 [2, 20, "bb"], 
 [3, 20, "cc"], 
 [4, 30, "dd"], 
 [5, 30, "ee"], 
 [6, 30, "ff"] 
 ]); 
 cfloop(query=q, group="fk"){ 
 writeOutput("<strong>#fk#</strong>"); 
 cfloop(){ 
 writeOutput("#pk#:#data#<br>"); 
 } 
 writeOutput("<hr>"); 
 }

